void prefix_exit(Prefix* handle)
{
	/* Default exit sequence for statechart prefix */
	exseq_BOTON_DEL_ENCODER(handle);
	exseq_ENCODER(handle);
	exseq_LCD(handle);
	exseq_PUENTE_H(handle);
	exseq_CONTROL_DE_TEMPERATURAS(handle);
	exseq_UART(handle);
}

sc_boolean prefix_isActive(const Prefix* handle)
{
	sc_boolean result = bool_false;
	sc_integer i;
	
	for(i = 0; i < PREFIX_MAX_ORTHOGONAL_STATES; i++)
	{
		result = result || handle->stateConfVector[i] != Prefix_last_state;
	}
	
	return result;
}

/* 
 * Always returns 'false' since this state machine can never become final.
 */
sc_boolean prefix_isFinal(const Prefix* handle)
{
(void)(handle);
return bool_false;

}

void prefix_raiseTimeEvent(Prefix* handle, sc_eventid evid)
{
	if ( ((sc_intptr_t)evid) >= ((sc_intptr_t)&(handle->timeEvents))
		&&  ((sc_intptr_t)evid) < ((sc_intptr_t)&(handle->timeEvents)) + (unsigned)sizeof(PrefixTimeEvents))
		{
		*(sc_boolean*)evid = bool_true;
	}		
}

